import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';
import axios from 'axios'
import { Button } from '@material-ui/core';

const OnlineChallengeTrivia = () => {
    const [showRooms, setShowCounter] = useState(true);
    const [index, setIndex] = useState(-1);

    var rooms = [{'username': 'one', 'room': '1'}, 
    {'username': 'two', 'room': '2'}, 
    {'username': 'three', 'room': '3'}, 
    {'username': 'four', 'room': '4'}, 
    {'username': 'five', 'room': '5'}, 
    {'username': 'six', 'room': '6'}, 
    {'username': 'seven', 'room': '7'}, 
    {'username': 'eight', 'room': '8'}, 
    {'username': 'nine', 'room': '9'},
    {'username': 'ten', 'room': '10'}]

    return (
        <div className='online-challenge-trivia'>
            { showRooms ? (
                <div className='rooms'>
                    <h1>Pick someone to challenge!</h1>
                    <div className = 'room-buttons'>
                        <button type="room1" onClick={() => setIndex(0), () => showRooms(false)}>{rooms[0].username}</button>
                        <button type="room1" onClick={() => setIndex(1)}>{rooms[1].username}</button>
                        <button type="room1" onClick={() => setIndex(2)}>{rooms[2].username}</button>
                        <button type="room1" onClick={() => setIndex(3)}>{rooms[3].username}</button>
                        <button type="room1" onClick={() => setIndex(4)}>{rooms[4].username}</button>
                        <button type="room1" onClick={() => setIndex(5)}>{rooms[5].username}</button>
                        <button type="room1" onClick={() => setIndex(6)}>{rooms[6].username}</button>
                        <button type="room1" onClick={() => setIndex(7)}>{rooms[7].username}</button>
                        <button type="room1" onClick={() => setIndex(8)}>{rooms[8].username}</button>
                        <button type="room1" onClick={() => setIndex(9)}>{rooms[9].username}</button>
                    </div>
                </div>
            ) : (
                    <>

                    </>
                )}
        </div>
    );
}

export default OnlineChallengeTrivia;
