import React, { useEffect, useState } from "react";
import "./EditProfile.css";
import axios from "axios";

const EditProfile = () => {
  const [bio, setBio] = useState("");
  // const instance = axios.create({ baseURL: "http://localhost:8080/api/v1" });
  const loadData = async () => {
    // const res = await fetch("http://localhost:8080/api/v1/getUserInfo", {
    //   method: "GET",
    //   //headers: { "Content-Type": "application/json" },
    //   //body: JSON.stringify({ username: "aumpatel2" }),
    // });
    // const data = await res.json();
    // console.log(data);
    const request = await axios.get("http://localhost:8080/api/v1/getUserInfo");
    console.log(request.status);
    // axios
    //   .get("http://localhost:8080/api/v1/getUserInfo", {
    //     data: { username: "aumpatel2" },
    //   })
    //   .then((res) => {
    //     console.log(res);
    //   })
    //   .catch((err) => {
    //     console.log(err);
    //   });
  };
  useEffect(() => {
    // const xhr = new XMLHttpRequest();
    // xhr.open("GET", "http://localhost:8080/api/v1/getUserInfo");
    // xhr.responseType = "json";
    // xhr.onload = () => {
    //   console.log(xhr.response);
    // };

    // xhr.send(JSON.stringify({ username: "aumpatel2" }));
    // console.log("hey");

    loadData();
    console.log("hey");
  }, []);

  const updateBio = () => {
    // instance.post("/updateUserProfile", { biography: bio });
    // setBio("");
    var xhr = new XMLHttpRequest();
    xhr.open("POST", "http://localhost:8080/api/v1/updateUserData", true);
    xhr.send(JSON.stringify({ username: "aumpatel2", biography: bio }));
    setBio("");
  };

  return (
    <div className="centContainer">
      <h5>MY SPORTCRED ACCOUNT</h5>
      <h3>Update Biography</h3>
      <textarea
        placeholder=" Enter something about you..."
        name="bio"
        id="bio"
        value={bio}
        onChange={(e) => setBio(e.target.value)}
      />
      <button className="button1" onClick={updateBio}>
        Update
      </button>
      <br />
      <h3>update profile picture</h3>
      <p>coming soon...</p>
      <br />
      <h3>update questionnaire answers</h3>
      <form>
        <label htmlFor="favSport">favourite sport</label>
        <input
          className="survey-input"
          type="text"
          name="favSport"
          placeholder="Enter sport"
        />
        <label htmlFor="sportLevel">highest level of sport played</label>
        <input
          className="survey-input"
          type="text"
          name="sportLevel"
          placeholder="Enter highest level of sport played"
        />
        <label htmlFor="age">age</label>
        <input
          className="survey-input"
          type="text"
          name="age"
          placeholder="Enter age"
        />
        <label htmlFor="favSportTeam">team you are rooting for</label>
        <input
          className="survey-input"
          type="text"
          name="favSportTeam"
          placeholder="Enter favourite sport"
        />
        <label htmlFor="learnSport">sport you want to learn</label>
        <input
          className="survey-input"
          type="text"
          name="learnSport"
          placeholder="Enter sport you want to learn about"
        />
        <button type="submit" className="button1">
          Update
        </button>
      </form>
      <br />
      <h3>Update password</h3>
      <form>
        <label htmlFor="oldPass">Current Password</label>
        <input
          className="survey-input"
          type="text"
          name="oldPass"
          placeholder="Enter your current password"
        />
        <label htmlFor="newPass">New Password</label>
        <input
          className="survey-input"
          type="text"
          name="newPass"
          placeholder="Enter new password"
        />
        <label htmlFor="newPassConf">Confirm New Password</label>
        <input
          className="survey-input"
          type="text"
          name="newPassConf"
          placeholder="Write new password again"
        />
        <button type="submit" className="button1">
          Update
        </button>
      </form>
      <br />
      <hr />
      <button id="logOutBtn" className="button1">
        LOG OUT
      </button>
      <br />
    </div>
  );
};

export default EditProfile;
