import React, { useEffect, useState } from 'react';
import './OnlineChallengeTrivia.css'; 
import { NavLink } from 'react-router-dom';
import axios from 'axios'
import { Button } from '@material-ui/core';

const OnlineChallengeTrivia = () => {
    const [roomsInfo, setRoomInfo] = useState([false, -1]);

    var rooms = [{'username': 'one', 'room': '1'}, 
    {'username': 'two', 'room': '2'}, 
    {'username': 'three', 'room': '3'}, 
    {'username': 'four', 'room': '4'}, 
    {'username': 'five', 'room': '5'}, 
    {'username': 'six', 'room': '6'}, 
    {'username': 'seven', 'room': '7'}, 
    {'username': 'eight', 'room': '8'}, 
    {'username': 'nine', 'room': '9'},
    {'username': 'ten', 'room': '10'}]

    return (
        <div className='online-challenge-trivia'>
            { roomsInfo[0] ? (
                <div className='rooms'>
                    <h1>Pick someone to challenge!</h1>
                    <div className = 'room-buttons'>
                        <button type="room1" onClick={() => setRoomInfo([true, 0])}>{rooms[0].username}</button>
                        <button type="room1" onClick={() => setRoomInfo([true, 1])}>{rooms[1].username}</button>
                        <button type="room1" onClick={() => setRoomInfo([true, 2])}>{rooms[2].username}</button>
                        <button type="room1" onClick={() => setRoomInfo([true, 3])}>{rooms[3].username}</button>
                        <button type="room1" onClick={() => setRoomInfo([true, 4])}>{rooms[4].username}</button>
                        <button type="room1" onClick={() => setRoomInfo([true, 5])}>{rooms[5].username}</button>
                        <button type="room1" onClick={() => setRoomInfo([true, 6])}>{rooms[6].username}</button>
                        <button type="room1" onClick={() => setRoomInfo([true, 7])}>{rooms[7].username}</button>
                        <button type="room1" onClick={() => setRoomInfo([true, 8])}>{rooms[8].username}</button>
                        <button type="room1" onClick={() => setRoomInfo([true, 9])}>{rooms[9].username}</button>
                        <div className = 'back'><p><NavLink exact to='/trivia/online'>back</NavLink></p></div>
                    </div>
                </div>
            ) : (
                    <>
                        <div className = 'trivia-game'>
                            {console.log(roomsInfo[0])}
                            {console.log(roomsInfo[1])}
                        </div>
                    </>
                )}
        </div>
    );
}

export default OnlineChallengeTrivia;
