                },
                {
                    "answerText": Data.answer10Option4.answerText,
                    "isCorrect": Data.answer10Option4.isCorrect
                }
            ]
        },
    ]

    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [showScore, setShowScore] = useState(false);
    const [sendResults, setSendResults] = useState(0);
    const [score, setScore] = useState(0);

    // CHECKS IF THE USER INPUT IS A CORRECT ANSWER
    const handleAnswerOptionClick = (isCorrect) => {
        if (isCorrect) {
            setScore(score + 1);
        }

        const nextQuestion = currentQuestion + 1;
        if (nextQuestion < questions.length) {
            setCurrentQuestion(nextQuestion);
            setCounter(14)
        } else {
            setShowScore(true);
            setSendResults(1);
        }
    };

    // 14s TIMER PER QUESTION
    const [counter, setCounter] = React.useState(14);
    useEffect(() => {
        const timer = counter >= 0 && setInterval(() => setCounter(counter - 1), 1000);

        if (counter === -1) {
            const nextQuestion = currentQuestion + 1;
            if (nextQuestion < questions.length) {
                setCurrentQuestion(nextQuestion);
                setCounter(14)
            } else {
                setShowScore(true);
                setSendResults(1);

                if (score < oppScore) {
                    setEndOfGameMsg('Lost..');
                } else if (score === oppScore) {
                    setEndOfGameMsg('Tied.')
                }
            }
        }

        return () => clearInterval(timer);
    }, [counter]);

    // SEND RESULTS BACK TO DB
    useEffect(() => {
        if (sendResults === 1) {
            const url = 'http://localhost:8080/api/v1/sendFinalSoloScore'
            const headers = {
                'Content-Type': 'text/plain',
            }

            axios.post(url, { 'username': 'king', 'correctAnswers': score, 'totalAnswers': '10' }, { headers }
            )
                .then(res => {
                    console.log(res.data)
                })
        }

    }, [sendResults])


    return (
        <div className='online-challenge-trivia'>
            { index === -1 ? (
                <div className='rooms'>
                    <h1>Pick someone to challenge!</h1>
                    <div className='room-buttons'>
                        <button type="room1" onClick={() => setIndex(0)}>{rooms[0].username}</button>
                        <button type="room1" onClick={() => setIndex(1)}>{rooms[1].username}</button>
                        <button type="room1" onClick={() => setIndex(2)}>{rooms[2].username}</button>
